sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function(event,data) { 
		//console.log('Listener caught NoteID: ' + $rootScope.noteID);

		c.server.get({
			action:'getNote',
			noteID:$rootScope.noteID
		}).then(function(r){
			c.data.title = r.data.notes.title;
			c.data.note = r.data.notes.note;
			c.data.noteID = r.data.notes.sys_id;
		})
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.notes = {};



				//gs.addInfoMessage("input:"+ input.noteID);
				$sp.getRecordValues(data.notes,note, "title,note,sys_id");
			}
		}
	}
})();
